func:
msg.payload = {
    timestamp: Date.now(),
};
msg.topic = `${env.get('deviceID')}/DDEATH`;
return msg;

// Check if message is for this device
if (msg.topic === env.get('deviceID')){

    let tagMatched = false;
    let msgs = [];
    let metrics = flow.get('metrics');

    // Loop through all incoming metrics
    await Promise.all(msg.payload.metrics.map(async (newMetric) => {
        // Loop through all existing metrics
        await Promise.all(metrics.map((metric) => {
            // Check if there is a match and the value needs updating
            if (newMetric.name === metric.name && newMetric.value !== metric.value){

                // Update the metric timestamp
                const now = Date.now();
                metric.timestamp = now;
                newMetric.timestamp = now;
                // Update the metric value
                metric.value = newMetric.value;
                if (metric.value === null) {
                    newMetric.isNull = true
                    metric.isNull = true
                } else {
                    newMetric.isNull = false;
                    metric.isNull = false;
                }

                // If the device output message doesn't exist, prepare a prototype
                if (!tagMatched){
                    tagMatched = true;
                    // Prepare output messages
                    msgs = [
                        {payload: {timestamp: now, metrics:[]}, topic:`${env.get('deviceID')}/DDATA`},
                        {payload: {}, topic:""}
                    ];
                }
                // Push the metric change to the DDATA output message
                msgs[0].payload.metrics.push(newMetric);
                // Set the name and value of the device output tag
                msgs[1].payload[metric.name] = newMetric.value;
            }
        }));
    }));
    // If metric changes were applied, output the changes back to sparkplug
    // and to the device itself.
    if (tagMatched){
        flow.set('metrics', metrics);
        return msgs;
    }
}
